import NodeRSA from "node-rsa";
import { LoggingEvent } from 'log4js';
/**
 * log4js加密Layout
 */
declare class CryptoLayout {
    private static publicKey;
    /**
     * 秘钥路径
     * @param keyPath
     */
    constructor(keyPath: string);
    static colorizeStart(style: string): string;
    static colorizeEnd(style: string): string;
    static colorize(str: string, style: string): string;
    static timestampLevelAndCategory(loggingEvent: LoggingEvent, colour?: string): string;
    /**
     *  加密layout
     * @param loggingEvent
     * @returns
     */
    encryptLayout(loggingEvent: LoggingEvent): string;
    /**
     * rsa加密
     * @param plain
     * @returns
     */
    static rsaEncrypt(plain: NodeRSA.Data): string;
    /**
     * aes加密
     * @param key
     * @param data
     * @returns
     */
    static aesEncrypt(key: string, ...data: any[]): string;
}
export { CryptoLayout };
